mapper.select:
SELECT EVENT.EVENT_NUM, EVENT.EMP_NUM, event_title, event_cont, write_date, event_hits, del_check
FROM EVENT
WHERE EVENT.EVENT_NUM = #{no}

mapper.update:
	UPDATE EVENT SET EMP_NUM = #{emp_num},
                 EVENT_TITLE = #{title},
                 EVENT_CONT= #{cont}
	WHERE EVENT_NUM = #{no}

mapper.select:
SELECT CMNT_NUM, CLNT_NUM, EVENT_NUM, EMP_NUM, CMNT_CONT, DEL_CHECK
FROM EVENT_CMNT
WHERE EVENT_NUM = #{no}
