mapper.select:
SELECT EVENT.EVENT_NUM, EVENT.EMP_NUM, event_title, event_cont, write_date, event_hits, del_check
FROM EVENT
WHERE EVENT.EVENT_NUM = #{no}

mapper.update:
	UPDATE EVENT SET EMP_NUM = #{emp_num},
                 EVENT_TITLE = #{title},
                 EVENT_CONT= #{cont}
	WHERE EVENT_NUM = #{no}
