SELECT COUNT(*) AS CNT
FROM INDVDL_CLNT
WHERE DEL_CHECK = '1'
AND ID = #{id}
AND PW = #{pw}

SELECT COUNT(*) AS CNT
FROM INDVDL_CLNT
WHERE ID=#{signup_id}

		SELECT REPLACE(ID,SUBSTR(ID,1,2),'**') AS ID, CLNT_NAME, PHONE_NUM_1
		FROM INDVDL_CLNT
		WHERE DEL_CHECK='1'
        AND CLNT_NAME=#{name}
        AND PHONE_NUM_1=#{phone_num1}

SELECT COUNT(*) AS CNT
FROM INDVDL_CLNT
WHERE DEL_CHECK='1'
AND CLNT_NAME= #{name}
AND PHONE_NUM_1=#{phone_num1}

