mapper.select:
SELECT * FROM (SELECT C.MNG_NUM,C.CARD_NUM, C.CARD_NAME,E.EMP_NAME AS EMP_NAME , TO_CHAR(USE_START_DT,'YYYY-MM-DD')AS USE_START_DT, TO_CHAR(USE_END_DT,'YYYY-MM-DD')AS USE_END_DT, ROW_NUMBER()OVER(ORDER BY C.CARD_NUM DESC) AS RNUM FROM CARD C INNER JOIN EMP E ON C.EMP_NUM = E.EMP_NUM LEFT OUTER JOIN CARD_USER CU ON C.MNG_NUM = CU.MNG_NUM WHERE 1=1
AND CARD_NUM LIKE '%' || #{searchTxt} || '%'
AND CARD_NAME LIKE '%' || #{searchTxt} || '%'
) A WHERE A.RNUM BETWEEN #{startCount} AND #{endCount}

 UPDATE CARD AS A, CARD_USER AS B
 		 SET A.EMP_NUM = #{use_num},A.CARD_CMPNY_NUM =#{card_co} , A.CARD_NAME =#{card_name} ,A.CARD_NUM =#{card_code} ,
 		 A.ISSUE_DT =#{issue_dt} ,A.END_DT = #{end_dt}, A.DSCRD_DT =#{del_dt},B.EMP_NUM=#{card_user},
 		 B.USE_START_DT=#{use_start_dt},B.USE_END_DT=#{use_end_dt}  
WHERE A.MNG_NUM =#{no} AND B.MNG_NUM =#{no}
