mapper.select:
SELECT LL.LEAD_NUM, LL.CLNT_CMPNY_NAME, LL.CLNT_NAME, LL.EMP_NAME, LL.PRGRS_STS_NUM, LL.PSNUM, LL.LEAD_NAME, LL.RGSTRTN_DATE FROM ( SELECT L.LEAD_NUM, CC.CLNT_CMPNY_NAME, EC.CLNT_NAME, E.EMP_NAME, L.LEAD_NAME, L.PRGRS_STS_NUM, DECODE(L.PRGRS_STS_NUM, 1, '진행중', 2, '종료(영업기회 전환)', 3, '종료(영업기회 실패)') AS PSNUM, TO_CHAR(L.RGSTRTN_DATE, 'YYYY-MM-DD') AS RGSTRTN_DATE, ROW_NUMBER() OVER(ORDER BY L.LEAD_NUM DESC) AS RNUM FROM LEAD L LEFT OUTER JOIN EMP E ON L.MNGR_EMP_NUM = E.EMP_NUM INNER JOIN ENTRPRS_CLNT EC ON L.CLNT_NUM = EC.CLNT_NUM INNER JOIN CLNT_CMPNY CC ON EC.CLNT_CMPNY_NUM = CC.CLNT_CMPNY_NUM AND TO_CHAR(L.RGSTRTN_DATE, 'YYYY-MM-DD') BETWEEN #{searchDate} AND #{searchDate2}
AND L.PRGRS_STS_NUM = #{psNum}
AND E.EMP_NAME LIKE '%' || #{mngEmp} || '%'
AND (L.LEAD_NAME LIKE '%' || #{searchTxt} || '%' OR CC.CLNT_CMPNY_NAME LIKE '%' || #{searchTxt} || '%' OR EC.CLNT_NAME LIKE '%' || #{searchTxt} || '%' OR L.LEAD_NUM LIKE '%' || #{searchTxt} || '%')
AND L.LEAD_NAME LIKE '%' || #{searchTxt} || '%'
AND CC.CLNT_CMPNY_NAME LIKE '%' || #{searchTxt} || '%'
AND EC.CLNT_NAME LIKE '%' || #{searchTxt} || '%'
AND L.LEAD_NUM LIKE '%' || #{searchTxt} || '%'
) LL WHERE LL.RNUM BETWEEN #{startCount} AND #{endCount}

SELECT MNGR.EMP_NUM, MNGR.EMP_NAME, MNGR.RANK_NAME, MNGR.DEPT_NAME FROM ( SELECT E.EMP_NUM, E.EMP_NAME, R.RANK_NAME, D.DEPT_NUM, D.DEPT_NAME, ROW_NUMBER() OVER(ORDER BY E.EMP_NUM ASC) AS RNUM FROM EMP E INNER JOIN APNTM A ON E.EMP_NUM = A.EMP_NUM INNER JOIN DEPT D ON A.DEPT_NUM = D.DEPT_NUM INNER JOIN RANK R ON A.RANK_NUM = R.RANK_NUM WHERE D.DEPT_NAME LIKE '영%'
AND D.DEPT_NUM = #{deptS}
AND E.EMP_NUM LIKE '%' || #{searchT} || '%'
AND E.EMP_NAME LIKE '%' || #{searchT} || '%'
) MNGR WHERE MNGR.RNUM BETWEEN #{startCount} AND #{endCount}

SELECT CC.CLNT_CMPNY_NUM, CC.CLNT_CMPNY_NAME, CC.CLNT_CMPNY_CLSFY_NAME, CC.GRADE_NUM, CC.GRADE_NAME, CC.ADRS, CC.DTL_ADRS, CC.RD FROM ( SELECT CC.CLNT_CMPNY_NUM, CC.CLNT_CMPNY_NAME, CC.CLNT_CMPNY_CLSFY_NUM, CCC.CLNT_CMPNY_CLSFY_NAME, CCG.GRADE_NUM, CCG.GRADE_NAME, CC.ADRS, CC.DTL_ADRS, CC.RGSTRTN_DATE, ROW_NUMBER() OVER(ORDER BY CC.CLNT_CMPNY_NUM DESC) RNUM, TO_CHAR(CC.RGSTRTN_DATE, 'YYYY-MM-DD') AS RD FROM CLNT_CMPNY CC INNER JOIN CLNT_CMPNY_CLSFY CCC ON CC.CLNT_CMPNY_CLSFY_NUM = CCC.CLNT_CMPNY_CLSFY_NUM INNER JOIN CLNT_CMPNY_GRADE CCG ON CC.GRADE_NUM = CCG.GRADE_NUM AND CC.DEL_CHECK IS NULL WHERE 1 = 1
AND CC.CLNT_CMPNY_CLSFY_NUM = #{cccnType}
AND CC.CLNT_CMPNY_NAME LIKE '%' || #{ccSearchTxt} || '%'
) CC WHERE CC.RNUM BETWEEN #{startCount} AND #{endCount}

SELECT COUNT(*) AS CNT FROM CLNT_CMPNY CC INNER JOIN CLNT_CMPNY_CLSFY CCC ON CC.CLNT_CMPNY_CLSFY_NUM = CCC.CLNT_CMPNY_CLSFY_NUM INNER JOIN CLNT_CMPNY_GRADE CCG ON CC.GRADE_NUM = CCG.GRADE_NUM AND CC.DEL_CHECK IS NULL WHERE 1 = 1
AND CC.CLNT_CMPNY_CLSFY_NUM = #{cccnType}
AND CC.CLNT_CMPNY_NAME LIKE '%' || #{ccSearchTxt} || '%'

SELECT EC.CLNT_NUM, EC.CLNT_NAME, EC.CLNT_CMPNY_NUM, EC.CLNT_CMPNY_NAME, EC.GRADE_NUM, EC.GRADE_NAME, EC.RDATE FROM ( SELECT EC.CLNT_NUM, EC.CLNT_NAME, CC.CLNT_CMPNY_NUM, CC.CLNT_CMPNY_NAME, CCG.GRADE_NUM, CCG.GRADE_NAME, TO_CHAR(CC.RGSTRTN_DATE, 'YYYY-MM-DD') AS RDATE, ROW_NUMBER() OVER(ORDER BY EC.CLNT_NUM DESC) AS RNUM FROM ENTRPRS_CLNT EC INNER JOIN CLNT_CMPNY CC ON EC.CLNT_CMPNY_NUM = CC.CLNT_CMPNY_NUM INNER JOIN CLNT_CMPNY_GRADE CCG ON CC.GRADE_NUM = CCG.GRADE_NUM AND EC.DEL_CHECK IS NULL
AND (EC.CLNT_NAME LIKE '%' || #{ecSearchTxt} || '%' OR CC.CLNT_CMPNY_NAME LIKE '%' || #{ecSearchTxt} || '%')
) EC WHERE EC.RNUM BETWEEN #{startCount} AND #{endCount}
