mapper.select:
SELECT SS.SALES_NUM, SS.PRGRS_STS, SS.PRGRS_STS2, SS.LEAD_NAME, SS.CLNT_CMPNY_NAME, SS.CLNT_NAME, SS.EMP_NAME, SS.EXPCTN_LOAN_SCALE FROM ( SELECT S.SALES_NUM, S.MNGR_EMP_NUM, E.EMP_NUM, LDI.EXPCTN_LOAN_SCALE, E.EMP_NAME, L.LEAD_NAME, EC.CLNT_NAME, CC.CLNT_CMPNY_NAME, DECODE(S.PRGRS_STS_NUM, 4, '종료(성공)', 5, '종료(실패)', '진행중') AS PRGRS_STS, DECODE(S.PRGRS_STS_NUM, 0, '영업기회', 1, '제안', 2, '견적', 3, '계약') AS PRGRS_STS2, ROW_NUMBER() OVER(ORDER BY S.SALES_NUM DESC) AS RNUM FROM SALES S INNER JOIN EMP E ON S.MNGR_EMP_NUM = E.EMP_NUM INNER JOIN LEAD L ON S.LEAD_NUM = L.LEAD_NUM INNER JOIN ENTRPRS_CLNT EC ON EC.CLNT_NUM = L.CLNT_NUM INNER JOIN CLNT_CMPNY CC ON CC.CLNT_CMPNY_NUM = EC.CLNT_CMPNY_NUM LEFT OUTER JOIN LOAN_DTL_INFO LDI ON S.SALES_NUM = LDI.SALES_NUM WHERE 1 = 1
AND S.PRGRS_STS_NUM = '0'
AND S.PRGRS_STS_NUM = '1'
AND S.PRGRS_STS_NUM = '2'
AND S.PRGRS_STS_NUM = '3'
) SS WHERE SS.RNUM BETWEEN #{startCount} AND #{endCount}
